import React, { Component } from "react";
import ColorBlock from "./ColorBlock";
import { choice } from "./helpers";
import "./ColorBlock.css";

export default class BlocksContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: Array.from({ length: 24 }, () => choice(this.props.allColors))
    };
  }
  handleClick(idx) {
    this.setState(prevState => {
      let newColors = [...prevState.colors];
      newColors[idx] = choice(this.props.allColors);
      return { colors: newColors };
    });
  }
  render() {
    const coloredBlocks = this.state.colors.map((color, i) => (
      <ColorBlock
        key={i}
        backgroundColor={color}
        onClickProp={this.handleClick.bind(this, i)}
      />
    ));
    return <section className="BlocksContainer">{coloredBlocks}</section>;
  }
}

